import{_ as s,o as a,c as e,R as l}from"./chunks/framework.1625126e.js";const g=JSON.parse('{"title":"仓库","description":"","frontmatter":{},"headers":[],"relativePath":"case/case2.md","filePath":"case/case2.md","lastUpdated":1687426991000}'),t={name:"case/case2.md"},n=l('<h1 id="仓库" tabindex="-1">仓库 <a class="header-anchor" href="#仓库" aria-label="Permalink to &quot;仓库&quot;">​</a></h1><p><a href="https://github.com/spiritLHLS/ecs" target="_blank" rel="noreferrer">https://github.com/spiritLHLS/ecs</a></p><h2 id="前言" tabindex="-1">前言 <a class="header-anchor" href="#前言" aria-label="Permalink to &quot;前言&quot;">​</a></h2><p>支持系统：</p><p>Ubuntu 18+, Debian 8+, centos 7+, Fedora 22+, Almalinux 8.5+, OracleLinux 8+, RockyLinux 8+, AstraLinux CE, Arch, FreeBSD(前提已执行<code>pkg install -y curl bash</code>)</p><h1 id="目录" tabindex="-1">目录 <a class="header-anchor" href="#目录" aria-label="Permalink to &quot;目录&quot;">​</a></h1><ul><li><a href="#融合怪测评脚本">融合怪测评脚本</a><ul><li><a href="#交互形式">交互形式</a></li><li><a href="#无交互形式">无交互形式</a></li><li><a href="#说明">说明</a></li></ul></li><li><a href="#纯测IP质量">纯测IP质量</a></li><li><a href="#部分服务器运行测试有各类bug一键修复后再测试">部分服务器运行测试有各类bug一键修复后再测试</a></li><li><a href="#功能">功能</a></li><li><a href="#脚本概况">脚本概况</a></li></ul><h2 id="融合怪测评脚本" tabindex="-1">融合怪测评脚本 <a class="header-anchor" href="#融合怪测评脚本" aria-label="Permalink to &quot;融合怪测评脚本&quot;">​</a></h2><h3 id="交互形式" tabindex="-1">交互形式 <a class="header-anchor" href="#交互形式" aria-label="Permalink to &quot;交互形式&quot;">​</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">curl</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-L</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://gitlab.com/spiritysdx/za/-/raw/main/ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">chmod</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">+x</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span></span></code></pre></div><p>或</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">curl</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-L</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://github.com/spiritLHLS/ecs/raw/main/ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">chmod</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">+x</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span></span></code></pre></div><p>或</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">bash &lt;(wget -qO- bash.spiritlhl.net/ecs)</span></span></code></pre></div><h3 id="无交互形式" tabindex="-1">无交互形式 <a class="header-anchor" href="#无交互形式" aria-label="Permalink to &quot;无交互形式&quot;">​</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#82AAFF;">echo</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">|</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&lt;(</span><span style="color:#FFCB6B;">wget</span><span style="color:#C3E88D;"> -qO- --no-check-certificate https://gitlab.com/spiritysdx/za/-/raw/main/ecs.sh</span><span style="color:#89DDFF;">)</span></span></code></pre></div><p>或</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#82AAFF;">echo</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">|</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&lt;(</span><span style="color:#FFCB6B;">wget</span><span style="color:#C3E88D;"> -qO- --no-check-certificate https://github.com/spiritLHLS/ecs/raw/main/ecs.sh</span><span style="color:#89DDFF;">)</span></span></code></pre></div><p>或</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">echo 1 | bash &lt;(wget -qO- bash.spiritlhl.net/ecs)</span></span></code></pre></div><h3 id="说明" tabindex="-1">说明 <a class="header-anchor" href="#说明" aria-label="Permalink to &quot;说明&quot;">​</a></h3><p>融合怪的执行结果保存在当前路径下的test_result.txt中，运行完毕可用<code>cat test_result.txt</code>查看记录</p><p>融合怪的完整版和精简版运行完毕自动上传结果到pastebin并回传分享链接</p><p><strong>有时候想要测一些配置极其拉跨的机器时，推荐使用screen命令挂起执行选项1的融合怪，然后你可以关闭SSH连接，等待一段时间后使用<code>cat test_result.txt</code>查看运行的实时状况，这样可以避免IO或者CPU过于垃圾导致的测试过程中的SSH连接中断，就不会测一半啥都没了</strong></p><p>最烂机器测试的例子(跑了47分钟一样测完)：<a href="https://github.com/spiritLHLS/ecs/blob/main/lowpage/README.md" target="_blank" rel="noreferrer">跳转</a></p><p>使用<strong>CDN</strong>加速已支持<strong>国内</strong>和<strong>国外</strong>服务器测试，但国内受CDN连通性或国内机器带宽大小的限制加载会慢很多</p><h3 id="初次使用推荐查看" tabindex="-1">初次使用推荐查看 <a class="header-anchor" href="#初次使用推荐查看" aria-label="Permalink to &quot;初次使用推荐查看&quot;">​</a></h3><h4 id="融合怪测试说明以及部分测试结果的内容解释" tabindex="-1">融合怪测试说明以及部分测试结果的内容解释 <a class="header-anchor" href="#融合怪测试说明以及部分测试结果的内容解释" aria-label="Permalink to &quot;融合怪测试说明以及部分测试结果的内容解释&quot;">​</a></h4><p>除了已标注的原创内容，其余所有分区均为借鉴并进行优化修改后的版本，与原始对应的脚本不一样</p><p>系统基础信息测试融合了三家还有我自己修补的部分检测(systl、virt、NAT类型检测等)，应该是目前最全面的了</p><p>CPU测试使用sysbench测试得分，不是yabs的gb4或gb5，前者只是简单的计算质数测试速度快，后者geekbench是综合测试系统算加权得分，不是同一种东西，别互相比较了，没有任何用处</p><p>CPU测试单核得分在5000以上的可以算第一梯队，4000到5000分算第二梯队，每1000分算一档，自己看看自己在哪个档位吧</p><p>AMD 7950x单核满血性能得分在6500左右，AMD 5950x单核满血性能得分5700左右，Intel普通的CPU(E5之类的)在1000~800左右，低于500的单核CPU可以算是比较烂的了</p><p>IO测试收录了两种，来源于lemonbench的dd磁盘测试和yabs的fio磁盘测试，综合来看会比较好，前者可能误差偏大但测试速度快无硬盘大小限制，后者真实一点但测试速度慢有硬盘以及内存大小限制</p><p>流媒体测试收录了两种，一个是go编译的二进制文件和一个shell脚本版本，二者各有优劣，互相对比看即可</p><p>tiktok测试有superbench和lmc999两种版本，哪个失效了随时可能更新为其中一种版本，以最新的脚本为准</p><p>回程路由测试选用的GO编译的二进制版本和朋友PR的版本，本人做了优化适配多个IP列表以及融合部分查询</p><p>IP质量检测纯原创，如有bug或者更多数据库来源可在issues中提出，日常看IP2Location数据库的IP类型即可</p><p>融合怪的IP质量检测是简化过的，没有查询Cloudflare的威胁得分，个人原创区的IP质量检测才是完整版(或者仓库说明中列出的那个IP质量检测的命令也是完整版)</p><p>三网测速使用自写的测速脚本，尽量使用最新节点最新组件进行测速，且有备用go版本测速，做到自更新测速节点列表，自适应系统环境测速</p><p>其他第三方脚本归纳到了第三方脚本区，里面有同类型脚本不同作者的各种竞品脚本，如果融合怪不能使你满意或者有错误，可以看看那部分</p><p>原创脚本区是个人原创的部分，有事没事也可以看看，可能会更新某些偏门或者独到的脚本</p><p>VPS测试，VPS测速，VPS综合性能测试，VPS回程线路测试，VPS流媒体测试等所有测试融合的脚本，本脚本能融合的都融合了</p><h3 id="纯测ip质量" tabindex="-1">纯测IP质量 <a class="header-anchor" href="#纯测ip质量" aria-label="Permalink to &quot;纯测IP质量&quot;">​</a></h3><ul><li>IP黑 <code>OR</code> 白</li><li>含IPV4 <code>AND</code> IPV6</li></ul><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&lt;(</span><span style="color:#FFCB6B;">wget</span><span style="color:#C3E88D;"> -qO- --no-check-certificate https://gitlab.com/spiritysdx/za/-/raw/main/qzcheck.sh</span><span style="color:#89DDFF;">)</span></span></code></pre></div><p>或</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&lt;(</span><span style="color:#FFCB6B;">wget</span><span style="color:#C3E88D;"> -qO- bash.spiritlhl.net/ecs-ipcheck</span><span style="color:#89DDFF;">)</span></span></code></pre></div><p>或</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&lt;(</span><span style="color:#FFCB6B;">wget</span><span style="color:#C3E88D;"> -qO- --no-check-certificate https://raw.githubusercontent.com/spiritLHLS/ecs/main/qzcheck.sh</span><span style="color:#89DDFF;">)</span></span></code></pre></div><h3 id="功能" tabindex="-1">功能 <a class="header-anchor" href="#功能" aria-label="Permalink to &quot;功能&quot;">​</a></h3><ul><li>[x] 自由组合测试方向和单项测试以及合集收录第三方脚本</li><li>[x] 基础系统信息--感谢teddysun和superbench和yabs开源，本人整理修改优化</li><li>[x] CPU测试--感谢lemonbench开源，本人整理修改优化</li><li>[x] 内存测试--感谢lemonbench开源，本人整理修改优化</li><li>[x] 磁盘dd读写测试--感谢lemonbench开源，本人整理修改优化</li><li>[x] 硬盘fio读写测试--感谢yabs开源<a href="https://github.com/masonr/yet-another-bench-script" target="_blank" rel="noreferrer">项目</a>，本人整理修改优化</li><li>[x] 御三家流媒体解锁测试--感谢sjlleo的<a href="https://github.com/sjlleo?tab=repositories" target="_blank" rel="noreferrer">二进制文件</a>，本人修改整理优化</li><li>[x] 常用流媒体解锁测试--感谢RegionRestrictionCheck的<a href="https://github.com/lmc999/RegionRestrictionCheck" target="_blank" rel="noreferrer">项目</a>，本人整理修改优化</li><li>[x] Tiktok解锁--感谢lmc999的<a href="https://github.com/lmc999/TikTokCheck" target="_blank" rel="noreferrer">项目</a>，本人整理修改优化</li><li>[x] 三网回程以及路由延迟--感谢zhanghanyun/backtrace开源<a href="https://github.com/zhanghanyun/backtrace" target="_blank" rel="noreferrer">项目</a>，本人整理修改</li><li>[x] 回程路由以及带宽类型检测(商宽/家宽/数据中心)--由fscarmen的PR以及本人的技术思路提供，本人整理修改优化</li><li>[x] IP质量检测(检测IP白不白)(含IPV4和IPV6)--本脚本独创，感谢互联网提供的查询资源</li><li>[x] speedtest测速--使用自写<a href="https://github.com/spiritLHLS/ecsspeed" target="_blank" rel="noreferrer">ecsspeed</a>仓库，自动更新测速服务器ID，一劳永逸解决老是要手动更新测速ID的问题</li></ul><h3 id="脚本概况" tabindex="-1">脚本概况 <a class="header-anchor" href="#脚本概况" aria-label="Permalink to &quot;脚本概况&quot;">​</a></h3><p>主界面：</p><p><img src="https://user-images.githubusercontent.com/103393591/233968968-e06be6c5-981e-440a-b519-b5d9a219b96a.png" alt="图片"></p><p>选项1融合怪完全体：</p><p><img src="https://user-images.githubusercontent.com/103393591/233967406-d2b74a20-8d95-4c18-8df1-254b97942f7d.png" alt="图片"><img src="https://user-images.githubusercontent.com/103393591/233968402-135c3f5e-def7-4f6e-a113-06d627780323.png" alt="图片"><img src="https://user-images.githubusercontent.com/103393591/233968486-60ccc9b7-2e4f-4cac-838f-fb4617eb118d.png" alt="图片"><img src="https://user-images.githubusercontent.com/103393591/233968544-b9d6f9ae-416a-4832-a516-8afb3846e33e.png" alt="图片"><img src="https://user-images.githubusercontent.com/103393591/233968662-f6174bed-1c72-4385-8716-8e1f9b9c4dce.png" alt="图片"><img src="https://user-images.githubusercontent.com/103393591/233968669-f58624ba-2611-4232-9326-0ce3e9d02d44.png" alt="图片"><img src="https://user-images.githubusercontent.com/103393591/233968819-f25086df-b3cd-4103-995a-8cc8512fe89d.png" alt="图片"></p><p>选项6原创区：</p><p><img src="https://user-images.githubusercontent.com/103393591/233969037-17d3d8e9-e42e-4314-ab89-13f76ea9fd98.png" alt="图片"></p><h2 id="致谢" tabindex="-1">致谢 <a class="header-anchor" href="#致谢" aria-label="Permalink to &quot;致谢&quot;">​</a></h2><p>感谢 ipinfo.io ip.sb ipip.net cip.cc cheervision.co 等网站提供的检测API</p><p>感谢所有开源项目提供的原始测试脚本</p><p>同时感谢以下平台提供编辑和测试支持</p><p><img src="https://resources.jetbrains.com/storage/products/company/brand/logos/PyCharm.png" alt="PyCharm logo"></p>',64),o=[n];function p(c,r,i,h,b,d){return a(),e("div",null,o)}const u=s(t,[["render",p]]);export{g as __pageData,u as default};
