import{_ as s,v as a,b as e,R as t}from"./chunks/framework.70afa331.js";const d=JSON.parse('{"title":"仓库","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"case/case2.md","filePath":"case/case2.md","lastUpdated":1698247766000}'),p={name:"case/case2.md"},l=t('<h1 id="仓库" tabindex="-1">仓库 <a class="header-anchor" href="#仓库" aria-label="Permalink to &quot;仓库&quot;">​</a></h1><p><a href="https://github.com/spiritLHLS/ecs" target="_blank" rel="noreferrer">https://github.com/spiritLHLS/ecs</a></p><p><a href="https://hits.seeyoufarm.com" target="_blank" rel="noreferrer"><img src="https://hits.seeyoufarm.com/api/count/incr/badge.svg?url=https%3A%2F%2Fgithub.com%2FspiritLHLS%2Fecs&amp;count_bg=%2379C83D&amp;title_bg=%23555555&amp;icon=&amp;icon_color=%23E7E7E7&amp;title=hits&amp;edge_flat=false" alt="Hits"></a></p><h1 id="融合怪测评脚本" tabindex="-1">融合怪测评脚本 <a class="header-anchor" href="#融合怪测评脚本" aria-label="Permalink to &quot;融合怪测评脚本&quot;">​</a></h1><p>支持系统：</p><p>Ubuntu 18+, Debian 8+, Centos 7+, Fedora 33+, Almalinux 8.5+, OracleLinux 8+, RockyLinux 8+, AstraLinux CE, Arch</p><p>半支持系统：</p><p>FreeBSD(前提已执行<code>pkg install -y curl bash</code>)，Armbian</p><p>Armbian系统部分检测和测试暂不支持，部分会编码错误</p><p>FreeBSD系统的硬盘测试和CPU测试目前是半残的，有些东西显示有问题</p><p>FreeBSD系统的分享链接的预处理部分sed命令存在问题未删除部分无效内容</p><p>支持架构：</p><p>基本都支持，无论是本地服务器还是云端服务器</p><p>支持地域：</p><p>能连得上网都支持</p><h2 id="部分服务器运行测试有各类bug一键修复后再测试" tabindex="-1">部分服务器运行测试有各类bug一键修复后再测试 <a class="header-anchor" href="#部分服务器运行测试有各类bug一键修复后再测试" aria-label="Permalink to &quot;部分服务器运行测试有各类bug一键修复后再测试&quot;">​</a></h2><p>一键修复各种系统原生bug的仓库：</p><p><a href="https://github.com/spiritLHLS/one-click-installation-script" target="_blank" rel="noreferrer">https://github.com/spiritLHLS/one-click-installation-script</a></p><p>如若还有系统bug请到上面仓库的issues反映，脚本原生BUG该仓库issues反映</p><h2 id="融合怪命令" tabindex="-1">融合怪命令 <a class="header-anchor" href="#融合怪命令" aria-label="Permalink to &quot;融合怪命令&quot;">​</a></h2><h3 id="交互形式" tabindex="-1">交互形式 <a class="header-anchor" href="#交互形式" aria-label="Permalink to &quot;交互形式&quot;">​</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">curl</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-L</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://gitlab.com/spiritysdx/za/-/raw/main/ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">chmod</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">+x</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span></span></code></pre></div><p>或</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">curl</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-L</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://github.com/spiritLHLS/ecs/raw/main/ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">chmod</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">+x</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span></span></code></pre></div><p>或</p><div class="language-"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">bash &lt;(wget -qO- bash.spiritlhl.net/ecs)</span></span></code></pre></div><h3 id="无交互形式-参数模式" tabindex="-1">无交互形式-参数模式 <a class="header-anchor" href="#无交互形式-参数模式" aria-label="Permalink to &quot;无交互形式-参数模式&quot;">​</a></h3><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">curl</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-L</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://gitlab.com/spiritysdx/za/-/raw/main/ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">chmod</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">+x</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">0</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">0</span></span></code></pre></div><p>或</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">curl</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-L</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">https://github.com/spiritLHLS/ecs/raw/main/ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">chmod</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">+x</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">0</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">0</span></span></code></pre></div><p>或</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">ecs.sh</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">1</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">0</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">0</span></span></code></pre></div><p>上述命令默认执行融合怪全体，输入的三个参数数字是对应的选项序号。</p><p><code>参数1 参数2 参数3</code>对应有交互模式中你依次输入的选项序号，如果有参数没用到留空或者写0即可。</p><p><code>参数2</code>可传入你本地的IPV4地址，将使用你本地IPV4作为回程路由测试的目标地址。</p><h2 id="ip质量检测" tabindex="-1">IP质量检测 <a class="header-anchor" href="#ip质量检测" aria-label="Permalink to &quot;IP质量检测&quot;">​</a></h2><ul><li>IP质量检测，含多家数据库查询，含黑名单查询</li><li>含 <code>IPV4</code> 和 <code>IPV6</code> 检测，含ASN和地址查询</li><li>含25端口的邮箱可达性检测，如果某个邮箱可达，则可搭建邮局</li></ul><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&lt;(</span><span style="color:#FFCB6B;">wget</span><span style="color:#C3E88D;"> -qO- --no-check-certificate https://gitlab.com/spiritysdx/za/-/raw/main/qzcheck.sh</span><span style="color:#89DDFF;">)</span></span></code></pre></div><p>或</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&lt;(</span><span style="color:#FFCB6B;">wget</span><span style="color:#C3E88D;"> -qO- bash.spiritlhl.net/ecs-ipcheck</span><span style="color:#89DDFF;">)</span></span></code></pre></div><p>或</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#FFCB6B;">bash</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&lt;(</span><span style="color:#FFCB6B;">wget</span><span style="color:#C3E88D;"> -qO- --no-check-certificate https://raw.githubusercontent.com/spiritLHLS/ecs/main/qzcheck.sh</span><span style="color:#89DDFF;">)</span></span></code></pre></div><h2 id="融合怪说明" tabindex="-1">融合怪说明 <a class="header-anchor" href="#融合怪说明" aria-label="Permalink to &quot;融合怪说明&quot;">​</a></h2><p>融合怪脚本最好在 /root 路径下执行，避免各种奇奇怪怪的问题</p><p>融合怪的执行结果保存在当前路径下的test_result.txt中，可先退出SSH登录后续再查看文件</p><p>融合怪的完整版和精简版运行完毕会自动上传结果到pastebin并回传分享链接，如果测一半想要退出，那么按<code>Ctrl+C</code>同时按下可终止测试，此时会自动退出删除残余文件</p><p><strong>有时候想要测一些配置极其拉跨的机器时，推荐使用screen命令挂起执行选项1的融合怪，然后你可以关闭SSH连接，等待一段时间后使用<code>cat test_result.txt</code>查看运行的实时状况，这样可以避免IO或者CPU过于垃圾导致的测试过程中的SSH连接中断，就不会测一半啥都没了，假如screen中显示乱码，也没问题，结果是不带乱码的</strong></p><p>最烂机器测试的例子(跑了47分钟一样测完)：<a href="https://github.com/spiritLHLS/ecs/blob/main/lowpage/README.md" target="_blank" rel="noreferrer">跳转</a></p><p>使用<strong>CDN</strong>加速已支持<strong>国内</strong>和<strong>国外</strong>服务器测试，但国内受CDN连通性或国内机器带宽大小的限制加载可能会慢很多</p><p>融合怪测试说明以及部分测试结果的内容解释(初次使用推荐查看)：</p><p>除了已标注的原创内容，其余所有分区均为借鉴并进行优化修改后的版本，与原始对应的脚本不一样</p><p>所有检测都有考虑过使用并行测试，并在部分环节使用了该技术，比正常的顺序执行优化了2~3分钟，属于是独有的，暂无哪家的测试有同类技术</p><p>系统基础信息测试融合了多家还有我自己修补的部分检测(systl、NAT类型检测，并发ASN检测等)，应该是目前最全面最通用的了</p><p>CPU测试使用sysbench测试得分，不是yabs的gb4或gb5，前者只是简单的计算质数测试速度快，后者geekbench是综合测试系统算加权得分，不是同一种东西，别互相比较了，没有可比性</p><p>CPU测试单核得分在5000以上的可以算第一梯队，4000到5000分算第二梯队，每1000分算一档，自己看看自己在哪个档位吧</p><p>AMD的7950x单核满血性能得分在6500左右，AMD的5950x单核满血性能得分5700左右，Intel普通的CPU(E5之类的)在1000~800左右，低于500的单核CPU可以算是比较烂的了</p><p>IO测试收录了两种，来源于lemonbench的dd磁盘测试和yabs的fio磁盘测试，综合来看会比较好，前者可能误差偏大但测试速度快无硬盘大小限制，后者真实一点但测试速度慢有硬盘以及内存大小限制</p><p>流媒体测试收录了两种，一个是go编译的二进制文件和一个shell脚本版本，二者各有优劣，互相对比看即可</p><p>tiktok测试有superbench和lmc999两种版本，哪个失效了随时可能更新为其中一种版本，以最新的脚本为准</p><p>回程路由测试选用的GO编译的二进制版本和朋友PR的版本，本人做了优化适配多个IP列表以及融合部分查询</p><p>IP质量检测纯原创，如有bug或者更多数据库来源可在issues中提出，日常看IP2Location数据库的IP类型即可，其中的25端口邮箱可达，则可搭建邮局</p><p>融合怪的IP质量检测是简化过的，没有查询Cloudflare的威胁得分，个人原创区的IP质量检测才是完整版(或者仓库说明中列出的那个IP质量检测的命令也是完整版)</p><p>三网测速使用自写的测速脚本，尽量使用最新节点最新组件进行测速，且有备用go版本测速，做到自更新测速节点列表，自适应系统环境测速</p><p>其他第三方脚本归纳到了第三方脚本区，里面有同类型脚本不同作者的各种竞品脚本，如果融合怪不能使你满意或者有错误，可以看看那部分</p><p>原创脚本区是个人原创的部分，有事没事也可以看看，可能会更新某些偏门或者独到的脚本</p><p>VPS测试，VPS测速，VPS综合性能测试，VPS回程线路测试，VPS流媒体测试等所有测试融合的脚本，本脚本能融合的都融合了</p><h2 id="融合怪功能" tabindex="-1">融合怪功能 <a class="header-anchor" href="#融合怪功能" aria-label="Permalink to &quot;融合怪功能&quot;">​</a></h2><ul><li>[x] 自由组合测试方向和单项测试以及合集收录第三方脚本</li><li>[x] 基础信息查询--感谢<a href="https://github.com/teddysun/across/blob/master/bench.sh" target="_blank" rel="noreferrer">bench.sh</a>、<a href="https://www.oldking.net/350.html" target="_blank" rel="noreferrer">superbench.sh</a>、<a href="https://github.com/masonr/yet-another-bench-script" target="_blank" rel="noreferrer">yabs</a>、<a href="https://github.com/LemonBench/LemonBench" target="_blank" rel="noreferrer">lemonbench</a>开源，本人整理修改优化，同原版均不一致</li><li>[x] CPU测试--感谢<a href="https://github.com/LemonBench/LemonBench" target="_blank" rel="noreferrer">lemonbench</a>开源，本人整理修改优化</li><li>[x] 内存测试--感谢<a href="https://github.com/LemonBench/LemonBench" target="_blank" rel="noreferrer">lemonbench</a>开源，本人整理修改优化</li><li>[x] 磁盘dd读写测试--感谢<a href="https://github.com/LemonBench/LemonBench" target="_blank" rel="noreferrer">lemonbench</a>开源，本人整理修改优化</li><li>[x] 硬盘fio读写测试--感谢<a href="https://github.com/masonr/yet-another-bench-script" target="_blank" rel="noreferrer">yabs</a>开源，本人整理修改优化</li><li>[x] 御三家流媒体解锁测试--感谢<a href="https://github.com/sjlleo?tab=repositories" target="_blank" rel="noreferrer">sjlleo的二进制文件</a>，本人修改整理优化</li><li>[x] 常用流媒体解锁测试--感谢<a href="https://github.com/lmc999/RegionRestrictionCheck" target="_blank" rel="noreferrer">RegionRestrictionCheck</a>开源，本人整理修改优化</li><li>[x] Tiktok解锁--感谢<a href="https://github.com/lmc999/TikTokCheck" target="_blank" rel="noreferrer">TikTokCheck</a>开源，本人整理修改优化</li><li>[x] 三网回程以及路由延迟--感谢<a href="https://github.com/zhanghanyun/backtrace" target="_blank" rel="noreferrer">zhanghanyun/backtrace</a>开源，本人整理修改</li><li>[x] 回程路由以及带宽类型检测(商宽/家宽/数据中心)--由<a href="https://github.com/fscarmen" target="_blank" rel="noreferrer">fscarmen</a>的PR以及本人的技术思路提供，本人后续修改优化</li><li>[x] IP质量与25端口检测(含IPV4和IPV6)--本脚本独创，感谢互联网提供的查询资源</li><li>[x] speedtest测速--使用自写<a href="https://github.com/spiritLHLS/ecsspeed" target="_blank" rel="noreferrer">ecsspeed</a>仓库，自动更新测速服务器ID，一劳永逸解决老是要手动更新测速ID的问题</li></ul><h1 id="友链" tabindex="-1">友链 <a class="header-anchor" href="#友链" aria-label="Permalink to &quot;友链&quot;">​</a></h1><h2 id="测评频道" tabindex="-1">测评频道 <a class="header-anchor" href="#测评频道" aria-label="Permalink to &quot;测评频道&quot;">​</a></h2><h3 id="https-t-me-vps-reviews" tabindex="-1"><a href="https://t.me/vps_reviews" target="_blank" rel="noreferrer">https://t.me/vps_reviews</a> <a class="header-anchor" href="#https-t-me-vps-reviews" aria-label="Permalink to &quot;https://t.me/vps_reviews&quot;">​</a></h3><h2 id="自动更新测速服务器节点列表的网络基准测试脚本" tabindex="-1">自动更新测速服务器节点列表的网络基准测试脚本 <a class="header-anchor" href="#自动更新测速服务器节点列表的网络基准测试脚本" aria-label="Permalink to &quot;自动更新测速服务器节点列表的网络基准测试脚本&quot;">​</a></h2><h3 id="https-github-com-spiritlhls-ecsspeed" tabindex="-1"><a href="https://github.com/spiritLHLS/ecsspeed" target="_blank" rel="noreferrer">https://github.com/spiritLHLS/ecsspeed</a> <a class="header-anchor" href="#https-github-com-spiritlhls-ecsspeed" aria-label="Permalink to &quot;https://github.com/spiritLHLS/ecsspeed&quot;">​</a></h3><h1 id="脚本概况" tabindex="-1">脚本概况 <a class="header-anchor" href="#脚本概况" aria-label="Permalink to &quot;脚本概况&quot;">​</a></h1><p>主界面：</p><p><img src="https://github.com/spiritLHLS/ecs/assets/103393591/051f1a83-ecd6-4713-af2f-c8b494e33c7f" alt="图片"></p><p>选项1融合怪完全体：</p><p><img src="https://github.com/spiritLHLS/ecs/assets/103393591/a769cb11-b416-4d40-a78c-265549bc4d49" alt="图片"><img src="https://github.com/spiritLHLS/ecs/assets/103393591/291854bf-4760-4a7f-8fad-33a114a2ba46" alt="图片"><img src="https://github.com/spiritLHLS/ecs/assets/103393591/6cad0c32-2409-4a92-b2c7-435f8eb66b3c" alt="图片"><img src="https://github.com/spiritLHLS/ecs/assets/103393591/e5e486e8-0791-43d6-919e-63b420cec022" alt="图片"><img src="https://github.com/spiritLHLS/ecs/assets/103393591/7296621e-76c0-41f1-bd9c-e3e696301dcc" alt="图片"><img src="https://github.com/spiritLHLS/ecs/assets/103393591/08289d71-9f91-4597-bcb1-0909622e16d4" alt="图片"><img src="https://github.com/spiritLHLS/ecs/assets/103393591/3a53758e-5fab-4fc5-a0b6-651c2f6b79a3" alt="图片"></p><p>选项6原创区：</p><p><img src="https://github.com/spiritLHLS/ecs/assets/103393591/393db695-5c94-41a9-9b02-812ad9d64967" alt="图片"></p>',80),o=[l];function n(r,c,i,h,C,b){return a(),e("div",null,o)}const m=s(p,[["render",n]]);export{d as __pageData,m as default};
